package de.rub.nds.tlsattacker.core.protocol.parser;

import de.rub.nds.modifiablevariable.util.ArrayConverter;
import de.rub.nds.tlsattacker.core.constants.HandshakeMessageType;
import de.rub.nds.tlsattacker.core.constants.ProtocolVersion;
import de.rub.nds.tlsattacker.core.protocol.message.CertificateMessage;
import java.util.Arrays;
import java.util.Collection;
import static org.junit.Assert.*;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.junit.runners.Parameterized;

@RunWith(Parameterized.class)
public class CertificateMessageParserTest {

    @Parameterized.Parameters
    public static Collection<Object[]> generateData() {
        return Arrays
                .asList(new Object[][] {
                        {
                                ArrayConverter
                                        .hexStringToByteArray(""),
                                HandshakeMessageType.CERTIFICATE,
                                1009,
                                1006,
                                ArrayConverter
                                        .hexStringToByteArray(""),
                                ProtocolVersion.TLS12 },
                        {
                                ArrayConverter
                                        .hexStringToByteArray(""),
                                HandshakeMessageType.CERTIFICATE,
                                1009,
                                1006,
                                ArrayConverter
                                        .hexStringToByteArray(""),
                                ProtocolVersion.TLS10 },
                        {
                                ArrayConverter
                                        .hexStringToByteArray(""),
                                HandshakeMessageType.CERTIFICATE,
                                1009,
                                1006,
                                ArrayConverter
                                        .hexStringToByteArray(""),
                                ProtocolVersion.TLS11 }, });
    }

    private byte[] message;

    private HandshakeMessageType type;
    private int length;

    private int certificatesLength;
    private byte[] certificateBytes;
    private ProtocolVersion version;

    public CertificateMessageParserTest(byte[] message, HandshakeMessageType type, int length, int certificatesLength,
            byte[] certificateBytes, ProtocolVersion version) {
        this.message = message;
        this.type = type;
        this.length = length;
        this.certificatesLength = certificatesLength;
        this.certificateBytes = certificateBytes;
        this.version = version;
    }

    /**
     * Test of parse method, of class CertificateMessageParser.
     */
    @Test
    public void testParse() {
        CertificateMessageParser parser = new CertificateMessageParser(0, message, version);
        CertificateMessage certMessage = parser.parse();
        assertArrayEquals(certMessage.getCompleteResultingMessage().getValue(), message);
        assertTrue(certMessage.getLength().getValue() == length);
        assertTrue(certMessage.getType().getValue() == type.getValue());
        assertTrue(certMessage.getCertificatesListLength().getValue() == certificatesLength);
        assertArrayEquals(certMessage.getCertificatesListBytes().getValue(), certificateBytes);
    }

}
